template.value:
export const $ACTION_TYPE$_REQUEST = '$ACTION_TYPE$_REQUEST';
export const $ACTION_TYPE$_SUCCESS = '$ACTION_TYPE$_SUCCESS';
export const $ACTION_TYPE$_FAILURE = '$ACTION_TYPE$_FAILURE';

import * as types from '../constants/actionTypes';
import { call, put, take, fork } from 'redux-saga/effects';

$END$

function *root () {
  while (true) {
    yield take(types.$TYPE_NAME$);
  }
}

export default function *() {
  yield [
    fork(root)
  ];
}
